import React,  { useState } from 'react'

const Blog = ({ blog, likeBlog, deleteBlog, username}) => {
  const blogStyle = {
    paddingTop: 10,
    paddingLeft: 2,
    border: 'solid',
    borderWidth: 1,
    marginBottom: 5
  }

  // const handleDeleteBlog = (event) => {
  //   event.preventDefault()
  //   if (window.confirm(`Remove blog "${blog.title}" by ${blog.author}?`)) {
  //     deleteBlog()
  //   }
    
  // }

  const removeBlog = (event) => {
    event.preventDefault()
    handleRemove(blog)
  }


  
  const [visible, setVisible] = useState(false)
 
  let sameUser = false

  if (blog.user.username === username) {
    sameUser = true
  }

  const showWhenVisible = { display: visible ? '' : 'none' }
  const showWhenSameUser = { display: sameUser ? '' : 'none' }

  const toggleVisibility = () => {
    setVisible(!visible)
  }

  return (
    <div style={blogStyle}>
      <div>
        {blog.title} {blog.author}
        <button onClick={toggleVisibility}>{visible ? 'hide' : 'view'}</button>
      </div>
      <div style={showWhenVisible}>
        {blog.url}<br />
        {blog.author}<br />
        {blog.likes} likes <button onClick={likeBlog}>like</button><br />
        <button onClick={removeBlog}>Remove blog</button>
        {/* <button style = {showWhenSameUser} button onClick={handleDeleteBlog}>remove</button> */}
      </div>
    </div>
)}
export default Blog
