import React from 'react'

const [visible, setVisible] = useState(false)

const hideWhenVisible = { display: visible ? 'none' : '' }
const showWhenVisible = { display: visible ? '' : 'none' }

const toggleVisibility = () => {
  setVisible(!visible)
}

const Blog = ({ blog }) => {
  const blogStyle = {
    paddingTop: 10,
    paddingLeft: 2,
    border: 'solid',
    borderWidth: 1,
    marginBottom: 5
  }

  return (
    <div style={blogStyle}>
      <div> 
        {blog.title} {blog.author}
      </div>
      // ...
  </div>
)}
export default Blog
