// export default Blog


import React, { useState } from 'react'

const Blog = ({ blog, handleLike, handleRemove }) => {
  const [showBlog, setShowBlog] = useState('hidden')


  const handleShowChange = (event) => {
    event.preventDefault()
    if (showBlog === 'hidden') {
      setShowBlog('visible')
    } else {
      setShowBlog('hidden')
    }
  }

  const increaseLike = (event) => {
    event.preventDefault()
    handleLike(blog)
  }

  const removeBlog = (event) => {
    event.preventDefault()
    handleRemove(blog)
  }

  const AdditionalInfo = () => {
    if (showBlog === 'visible') {
      return (
        <div>
          {blog.url}
          <br />
          {blog.likes} likes <button onClick={increaseLike}>like</button>
          <button onClick={removeBlog}>Remove blog</button>
          <br />
        </div>)
    } else {
      return null
    }
  }

  return (
    <li className='blog'>      <div className='blogHeader' onClick={handleShowChange}>
      {blog.title}, {blog.author}
    </div>
    <AdditionalInfo />
    </li>
  )
}

export default Blog
